import 'package:flutter/material.dart';

class BrandLogo extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Container(
      padding: EdgeInsets.all(20.0),
      margin: EdgeInsets.all(20.0),
      child: Column(
        children: [
          SizedBox(height: 20.0),
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceEvenly,
            children: [
              buildBrandWidget("data:image/png;base64,", 'Nike'),
              buildBrandWidget("data:image/jpeg;base64,", 'Adidas'),
              buildBrandWidget("data:image/png;base64,", 'Puma'),
              buildBrandWidget("data:image/png;base64,", 'NB'),
              buildBrandWidget("data:image/png;base64,", 'Converse'),
            ],
          ),
        ],
      ),
    );
  }

  Widget buildBrandWidget(String imageUrl, String brandName) {
    return Column(
      children: [
        CircleAvatar(
          backgroundImage: NetworkImage(imageUrl),
          radius: 30,
          backgroundColor: Colors.grey[200],
        ),
        SizedBox(height: 8),
        Text(
          brandName,
          style: TextStyle(fontSize: 12),
        ),
      ],
    );
  }
}
